<template>
  <div class="gander">
    <div id="main" style="width: 900px;height:600px;"></div>
  </div>
</template>

<script>
import * as echarts from 'echarts'
export default {
  name: 'gander',
  methods: {
    myEcharts () {
      var chartDom = document.getElementById('main')
      var myChart = echarts.init(chartDom)
      var option
      const res = [{
        'label': '男',
        'value': 73189
      },
      {
        'label': '女',
        'value': 51593
      }
      ]
      const border =
        'data:image/png;base64,'
      const man =
        'data:image/png;base64,'
      const woman =
        'data:image/png;base64,'
      const color = ['#00FFFF', 'rgba(196,36,255,1)']
      const data = []
      for (let i = 0; i < res.length; i++) {
        data.push({
          value: res[i].value,
          name: res[i].label,
          itemStyle: {
            normal: {
              borderWidth: 5,
              shadowBlur: 20,
              borderColor: color[i],
              shadowColor: color[i]
            }
          }
        }, {
          value: res[0].value / 10,
          name: '',
          itemStyle: {
            normal: {
              label: {
                show: false
              },
              labelLine: {
                show: false
              },
              color: 'rgba(0, 0, 0, 0)',
              borderColor: 'rgba(0, 0, 0, 0)',
              borderWidth: 0
            }
          }
        })
      }
      option = {
        backgroundColor: '#0A2E5D',
        color,
        graphic: {
          elements: [{
            type: 'image',
            z: 3,
            style: {
              image: border,
              width: 300,
              height: 300
            },
            left: 'center',
            top: 'center',
            position: [100, 100]
          },
          {
            type: 'image',
            z: 3,
            style: {
              image: man,
              width: 85,
              height: 120
            },
            left: '36%',
            top: 'center'
          },
          {
            type: 'image',
            z: 3,
            style: {
              image: woman,
              width: 85,
              height: 120
            },
            right: '36%',
            top: 'center'
          }
          ]
        },
        tooltip: {
          show: false
        },
        toolbox: {
          show: false
        },
        series: [{
          name: '',
          type: 'pie',
          clockWise: false,
          radius: [175, 180],
          hoverAnimation: false,
          itemStyle: {
            normal: {
              label: {
                show: true,
                fontSize: 24,
                formatter (params) {
                  return params.name ? params.name + '\n' + params.value + '人' : ''
                }
              },
              labelLine: {
                width: 4,
                length: 30,
                length2: 50,
                show: true,
                color: '#00ffff'
              }
            }
          },
          data
        }]
      }

      option && myChart.setOption(option)
    }
  },
  mounted () {
    this.myEcharts()
  }
}
</script>

